{
  "i AM TOO YOUNG TO DIE",
  "hEY, NOT TOO ROUGH",
  "hURT ME PLENTY",
  "uLTRA-vIOLENCE"
},sel=2,max=2}},0

function _init()
  cartdata(mod_name)
  -- reset saved state
  dset(0,-1)

  -- 
  max_map_id=dget(32)
  if(max_map_id>#_maps_label) max_map_id=#_maps_label dset(32,max_map_id)
  if(max_map_id<=0) max_map_id=1 dset(32,max_map_id)
  menus[1].max=max_map_id

  max_skill=dget(33)
  if(max_skill>#menus[2][2]) max_skill=#menus[2][2] dset(33,max_skill)
  if(max_skill<=0) max_skill=2 dset(33,max_skill)
  menus[2].max=max_skill

  cls()
  anm_ttl=0
  
  -- end of game
  endstate=0
  gfx_str=gfx
  if(stat(6)=="gameover") endstate=1 gfx_str=gameover_gfx
  if(stat(6)=="endgame") endstate=2 gfx_str=endgame_gfx

  -- copy image to memory
	local addr=0x4300
	for i=0,#gfx_str\4-1 do
		local b=tonum("0x"..sub(gfx_str,i*4+1,i*4+4))
    poke2(addr+i*2,b)
    if (i==2047)addr=-0x1000
  end
  addr=0x3200
  for i=0,#snd\4-1 do
		local b=tonum("0x"..sub(snd,i*4+1,i*4+4))
    poke2(addr+i*2,b)
	end
end

function _update60()
  anm_ttl=(anm_ttl+1)%48
  if load_ttl then
    load_ttl-=1
    if load_ttl==0 then
      -- load game "group" cart
      local sel=menus[1].sel
      load(_maps_group[sel]..".p8",nil,menus[2].sel..","..sel)
    end
  else
    if menu_i>0 then
      active_sel=menus[menu_i].sel
      if btnp(2) then
        active_sel-=1
        sfx(0)
      end
      if btnp(3) then
        active_sel+=1
        sfx(0)
      end
      -- unlocked?
      menus[menu_i].sel=mid(active_sel,1,menus[menu_i].max)
    end
    if btnp(4) or btnp(5) then
      if(menu_i>0)sfx(1)
      menu_i+=1
      if menu_i>#menus then
        load_ttl=30
      end
    end
  end
end

function _draw()
  cls()
  memcpy(0x6000,0x4300,0x1000)
  sspr(0,0,128,64,0,64)
  if load_ttl then
    local s="eNTERING "..menus[1][2][menus[1].sel]
    printb(s,63-#s*2,80,15)
  elseif menu_i==0 then
    if endstate==1 then
      printb("game over",47,96,15)
    elseif endstate==2 then
      printb("victory",49,96,15)
    else
      printb("press ",47,121,15)
    end
  else
    -- dark menu background
    for i=1,16 do
      pal(i-1,pd[i])
    end
    sspr(12,0,104,19+#menus[menu_i][2]*9,12,64)
    pal()
    
    -- title
    printb(menus[menu_i][1],63-#menus[menu_i][1]*2,69,14)

    -- selection marker
    rectfill(18,70+menus[menu_i].sel*9,113,79+menus[menu_i].sel*9,1)
    palt(0,false)
    palt(4,true)
    sspr(anm_ttl\12*10,64,11,12,14,68+menus[menu_i].sel*9)
    palt()

    -- menu items
    for i=1,#menus[menu_i][2] do
      printb(menus[menu_i][2][i],28,72+i*9,i<=menus[menu_i].max and 15 or 3)
    end
    
    printb("@fsouchu",2,121,4)
    printb("@gamecactus",83,121,4)
  end
  for i=1,16 do
    pal(i-1,p[i],1)
  end
end

function printb(s,x,y,c,bgc)
  bgc=bgc or 0
  print(s,x,y+1,bgc)
  print(s,x,y,c)
end